import React, {Component} from 'react';
import CitySearch from './CitySearch';

class App extends Component {

    constructor(props){
        super(props);
        this.state = {
            city: '',
            temperature: '',
            units: '',
            symbol: '',
            message: ''
        }        
        this.handleSearch = this.handleSearch.bind(this);
    }

    handleSearch(city, temperature, units) {
        this.setState({
            city: city,
            temperature: temperature,
            units: units,
            message: `The temperature in ${city} is `,
            symbol: 'C'
        });
    }

    render () {
        return (
            <div className="App">
                <CitySearch 
                    onSearchComplete={this.handleSearch}
                />
                <p>{this.state.message}{this.state.temperature}{this.state.symbol}</p>
            </div>
        );
    }
}

export default App;
