import React, {Component} from 'react';

class App extends Component {

    constructor(props){
        super(props);
        this.state = {
            temperature: ''
        }
    }

    componentDidMount() {
        this.callAPI();
    }

    callAPI = () => {
        fetch('https://api.openweathermap.org/data/2.5/weather?q=london&units=metric&APPID=d5566182730361aa1c00849d2f86bb95')
        .then(result => result.json())
        .then((result) => {
            this.setState({ temperature: result.main.temp })
            console.log(result.main.temp)
        })
        .catch(err => { console.log(err)} )
    }

    render () {
        return (
            <p>{this.state.temperature}</p>
        );
    }
}

export default App;
