import React, {Component} from 'react';
import Form from './Form';

class App extends Component {

    constructor(props){
        super(props);
        this.state = {
            city: '',
            units: 'metric',
            temperature: ''
        }

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    componentDidMount() {
        // this.callAPI();
    }

    callAPI = () => {
        const url = `https://api.openweathermap.org/data/2.5/weather?q=${this.state.city}&units=${this.state.units}&APPID=d5566182730361aa1c00849d2f86bb95`;

        fetch(url)
            .then(result => result.json())
            .then((result) => {
            this.setState({ temperature: result.main.temp })
            console.log(result.main.temp)
        })
        .catch(err => { console.log(err)} )
    }

    handleChange(e) {
        this.setState({ 
          city: e.target.value 
        });
    }

    handleSubmit = (e) =>  {
        e.preventDefault();
        this.callAPI();
    }

    render () {
        return (
            <div className="App">
                <Form 
                    onSearchComplete={this.handleSearch}
                />
                <p>{this.state.temperature}</p>
            </div>
        );
    }
}

export default App;
