import React, {Component} from 'react';
import axios from 'axios';

class CitySearch extends Component {

    constructor(props){
        super(props)

        this.state = {
            city: '',            
            temperature: '',
            units: 'metric',
        }

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.callAPI = this.callAPI.bind(this);
    }

    callAPI = () => {
        const url = `https://api.openweathermap.org/data/2.5/weather?q=${this.state.city}&units=${this.state.units}&APPID=d5566182730361aa1c00849d2f86bb95`;     
        axios.get(url)

            .then((response) => {
                this.setState({ 
                    temperature: response.data.main.temp 
                });            
                this.props.onSearchComplete(this.state.city, this.state.temperature, this.state.units);
            })
            .catch((error) => { 
                if (error.response) {
                    /*
                     * The request was made and the server responded with a
                     * status code that falls out of the range of 2xx
                     */
                    console.log(error.response.data);
                    this.setState({
                    message: error.response.data.message
                    })
                    console.log(error.response.status);
                    console.log(error.response.headers);
                } else if (error.request) {
                    /*
                     * The request was made but no response was received, `error.request`
                     * is an instance of XMLHttpRequest in the browser and an instance
                     * of http.ClientRequest in Node.js
                     */
                    console.log(error.request);
                } else {
                    // Something happened in setting up the request and triggered an Error
                    console.log('Error', error.message);
                }
            })
        }

    componentDidMount() {
        this.setState({
            city: this.props.city
        });
    }

    handleChange(e) {
        this.setState({ 
          city: e.target.value 
        });
    }

    handleSubmit = (e) =>  {
        e.preventDefault();
        this.callAPI();
    }

    render () {
        return (
            <form onSubmit={this.handleSubmit}>
                <label 
                    htmlFor="name">Enter city: 
                </label>
                <input
                    id="city"
                    type="text"
                    value={this.state.city}
                    onChange={this.handleChange}
                />
                <div>
                <input 
                    type="radio" 
                    name="units" 
                    value="celcius" 
                    id="celcius"  
                />
                <label 
                    htmlFor="celcius">Celcius
                </label>
                <input 
                    type="radio" 
                    name="units" 
                    value="fahrenheit" 
                    id="fahrenheit" 
                />
                <label 
                    htmlFor="fahrenheit">Fahrenheit
                </label>
                </div>

                <button type="submit">Submit</button>
            </form>
        )
    }
}

export default CitySearch;
