import React, {Component} from 'react';
import './App.css';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      city: '',
      weather: '',
      temperature: '',
      units: 'metric',
      symbol: 'C'
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  componentDidMount(){
    // this.callAPI()
  }

  callAPI() {
    fetch('http://localhost:3001/routes/testapi.js')
        .then(response => response.json()) 
        .then(response => JSON.stringify(response.main.temp))
        .then(response => this.setState({ temperature: response }))
        .catch(err => console.log(err))
  }

  // callAPI = async () => {
  //   const response = await fetch('https://api.openweathermap.org/data/2.5/weather?q=london&APPID=d5566182730361aa1c00849d2f86bb95');
  //   const body = await response.json();
  //   if(response.status !== 200) throw Error(body.message);

  //   return body;
  // };

  handleChange(event) {
    this.setState({ 
      city: event.target.value 
    });
  }

  handleSubmit(event) {
    event.preventDefault();
    console.log(`${this.state.city}`);
    this.callAPI();
    // fetch(`/city?city=${encodeURIComponent(this.state.city)}`)
    //   .then(response => response.json())
    //   .then(state => this.setState(state));

    // const response = await fetch('api/weather', {
    //   method: 'POST',
    //   headers: {
    //     'Content-Type': 'application/json',
    //   },
    //   body: JSON.stringify({post: this.state.weather
    //   });
    //   const body = await response.text();

      this.setState({
        city: event.target.value
      });
    // })
    }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <form onSubmit={this.handleSubmit}>
            <label htmlFor="name">Enter city: </label>
            <input
              id="city"
              type="text"
              value={this.state.city}
              onChange={this.handleChange}
            />
            <div>
              <input 
                type="radio" 
                name="units" 
                value="celcius" 
                id="celcius"  
              />
              <label 
                htmlFor="celcius">Celcius
              </label>
              <input 
                type="radio" 
                name="units" 
                value="fahrenheit" 
                id="fahrenheit" 
              />
              <label 
                htmlFor="fahrenheit">Fahrenheit
              </label>
            </div>

            <button type="submit">Submit</button>
          </form>
          <p>Temperature for {this.state.city} is {this.state.temperature}&#176;{this.state.symbol}</p>
        </header>
      </div>
    );
  }
}

export default App;
