import React, {Component} from 'react';
import './App.scss';
import SunIcon from './images/sun.png';
import CitySearch from './CitySearch';

class App extends Component {

    constructor(props){
        super(props);
        this.state = {
            city: '',
            temperature: '',
            units: '',
            symbol: '',
            message: '',
            error: ''
        }        
        this.handleSearch = this.handleSearch.bind(this);
    }

    handleSearch(city, temperature, units, error) {
        this.setState({
            city: city,
            temperature: temperature,
            units: units,
            message: `The temperature in ${city} is `,
            symbol: '°C',
            error: error
        });
    }

    render () {
        return (
            <div className="App">
                <header className="App-header">
                    <div>
                       <img src={SunIcon} alt="sun icon" className="sun" />
                        <h1 className="title">Check the weather</h1>
                    </div>
                    <CitySearch 
                        onSearchComplete={this.handleSearch}
                    />
                    <p>{this.state.message}{this.state.temperature}{this.state.symbol}</p>

                    {/* { this.state.error.length >0 &&  */}
                         {/* <p>{this.state.error}</p> */}
                    {/* } */}
                </header>
            </div>
        );
    }
}

export default App;
