    //   this.setState({
    //     responseToPost: body
    //   });
    // })
    }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <form onSubmit={this.handleSubmit}>
            <label htmlFor="name">Enter city: </label>
            <input
              id="city"
              type="text"
              value={this.state.city}
              onChange={this.handleChange}
            />
            <button type="submit">Submit</button>
          </form>
          <p>{this.state.city}</p>
        </header>
      </div>
    );
  }
}

export default App;
