import React, {Component} from 'react';

class CitySearch extends Component {

    constructor(props){
        super(props)

        this.state = {
            city: '',            
            temperature: '',
            units: 'metric',
        }

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.callAPI = this.callAPI.bind(this);
    }

    callAPI = () => {
        const url = `https://api.openweathermap.org/data/2.5/weather?q=${this.state.city}&units=${this.state.units}&APPID=d5566182730361aa1c00849d2f86bb95`;

        fetch(url)
            .then((result) => {
                console.log(result)
                result.json();                
            })
            .then((result) => {
                this.setState({ 
                    temperature: result.main.temp 
                })
                console.log(result.main.temp);
                this.props.onSearchComplete(this.state.city, this.state.temperature, this.state.units);
            })
            .catch(err => { console.log(err)} )
    }

    componentDidMount() {
        this.setState({
            city: this.props.city
        });
    }

    handleChange(e) {
        this.setState({ 
          city: e.target.value 
        });
    }

    handleSubmit = (e) =>  {
        e.preventDefault();
        this.callAPI();
    }

    render () {
        return (
            <form onSubmit={this.handleSubmit}>
                <label 
                    htmlFor="name">Enter city: 
                </label>
                <input
                    id="city"
                    type="text"
                    value={this.state.city}
                    onChange={this.handleChange}
                />
                <div>
                <input 
                    type="radio" 
                    name="units" 
                    value="celcius" 
                    id="celcius"  
                />
                <label 
                    htmlFor="celcius">Celcius
                </label>
                <input 
                    type="radio" 
                    name="units" 
                    value="fahrenheit" 
                    id="fahrenheit" 
                />
                <label 
                    htmlFor="fahrenheit">Fahrenheit
                </label>
                </div>

                <button type="submit">Submit</button>
            </form>
        )
    }
}

export default CitySearch;
